function isLeaf(rowData, users){
  let data = hasChild(rowData, users)
  if(data){
    if(!rowData.referral_users && !data.isChild) return {bool:true}
    else return {bool:false, child:data.child}
  }
}

function hasChild(rowData, users){
  let child;
  let isChild = false;
  if(users && rowData){
    users.forEach((u) => {
      if (rowData.user_id === u.user_id) {
        child = u;
        isChild = true;
      }
    });
    return {isChild, child}
  }
}

export default isLeaf